mapper.select:
SELECT *, (SELECT album FROM album WHERE album_no=album.no) AS albumName, (SELECT artist FROM artist WHERE artist_no=artist.no) AS artistName
FROM song

ORDER BY ${orderby} ${direct}
LIMIT ${startIdx}, ${pageRow}

SELECT *, (SELECT album FROM album WHERE album_no=album.no) AS albumName, (SELECT artist FROM artist WHERE artist_no=artist.no) AS artistName
FROM song
WHERE no = #{no}

mapper.update:
UPDATE song
album_no = #{album_no}, artist_no = #{artist_no}, title = #{title}, genre = #{genre}, lyrics = #{lyrics},
src_org = #{src_org}, src_real = #{src_real}
WHERE no = #{no}
